import {BrowserRouter as Router,Routes,Route,Link} from "react-router-dom";
import { useState } from "react";
import Axios from "axios";

//styles
const background = {
    // backgroundImage: "url(" + { Background } + ")",
    // width: "600px",
    // height: "600px",
    // backgroundRepeat: "no-repeat"
    backgroundColor: '#003865'
}

const heading = {
  color: 'navy',
  textAlign: 'center',
  fontSize: '40px',
  fontFamily: 'Courier New '
}

const userPassText = {
  color: 'navy',
  textAlign: 'center',
  fontSize: '18px',
  fontFamily: 'Courier New',
  marginLeft: '100px'
}

const inputBarsRegistration = {
  width: '40%',
  padding: '12px 15px',
  margin: '8px 0',
  boxSizing: 'border-box',
  float: 'initial',
  marginTop: '50px',
  marginLeft: '315px'
}

const inputBarsLogin = {
  width: '40%',
  padding: '12px 15px',
  margin: '8px 0',
  boxSizing: 'border-box',
  float: 'initial',
  marginTop: '50px',
  marginLeft: '510px'
}

const regLogButton = {
  transitionDuration:' 0.4s',
  transform: 'translateY(4px)',
  fontFamily: 'Courier New',
  marginLeft: '795px',
  width: '10%',
  padding: '8px 18px',
  fontSize: '16px'

}



function AppRender (){

    //hooks
    const [usernameReg, setUsernameReg] = useState('')
    const [passwordReg, setPasswordReg] = useState('')
    const[ usernameLogin, setUsernameLogin] = useState('')
    const[ passwordLogin, setPasswordLogin] = useState('')
    const[loginStatus, setLoginStatus ] = useState('') 
    const[regStatus, setRegStatus] = useState('')

// function when we click on the register button sends all our info to backend so it inserts to our db
const register = () => {
    console.log("making the call client");
    Axios.post("http://localhost:3001/register", {
      username: usernameReg, 
      password: passwordReg
    }).then((response) => {
    if(response.data.message){
        console.log("username taken")
        setRegStatus(response.data.message)
    }
    else{
        console.log("succesfully created account")
        setRegStatus("Account Created")
    }
        console.log(response);
    });
};

// function when we click on the login button sends all our info to backend so we can compare if password username combo matches
const login = () => {
    console.log("making the call client");
     Axios.post("http://localhost:3001/login", {
       username: usernameLogin, 
       password: passwordLogin
     }).then((response) => {
       if(response.data.errormessage){ //if an error exists ie) errmsg is sent then display it on webpage
         console.log('wrong user/pass')
         setLoginStatus(response.data.errormessage)
       }
       else{//if no error
         // console.log("Printing response==> "+response.data[0].username);
         console.log('sucessful login')
         setLoginStatus(response.data[0].username)
       }
       // console.log(response.data);
     });
};

const registrationPage = () =>{
        <div>
            <div styles={background} className = "Registration">
            <h1 style = {heading}>Registration</h1>
            <label style = {userPassText}>Username</label>
            <input 
                style = {inputBarsRegistration} 
                type = "text" 
                onChange = {(e)=> {setUsernameReg(e.target.value);}}
            />
            <br/>
            <label style = {userPassText}>Password</label>
            <input
                style = {inputBarsRegistration} 
                type = "password"
                onChange = {(e) => {setPasswordReg(e.target.value);}}
            />
            <br/>
            <button 
                style = {regLogButton}
                onClick = {register}>Register
            </button>
            <br/>
            <br/>
            </div>
            <h1>{regStatus}</h1>
        </div>
}

const loginPage = () =>{

       <div>
        <div styles={background} className = "Login">
            <h1 style = {heading}>Login</h1>
            <label style = {heading}>       </label>
            <input 
                  style = {inputBarsLogin}
                  type = "text" 
                  placeholder = "Username..."
                  onChange = {(e)=> {setUsernameLogin(e.target.value);}}
            />
            <br/>
            <input 
                  style = {inputBarsLogin}
                  type = "password" 
                  placeholder = "Password..."
                  onChange = {(e)=> {setPasswordLogin(e.target.value);}}
            />
            <br/>
            <button 
                  style = {regLogButton}
                  onClick = {login}>Login
            </button>
        </div>
        <h1>{loginStatus}</h1>
      </div >
}
    return(
        <Router>
        <div>
            <ul>
              <li>
                <Link to="/loginPage" style ={heading} onClick={loginPage}>Login</Link>
              </li>
              <li>
                <Link to="/registrationPage" style ={heading} onClick = {registrationPage}>Registration</Link>
              </li>
            </ul>

            <hr />
            <Routes>
                <Route exact path="/loginPage" element={loginPage} />
                <Route path="/registrationPage" element={registrationPage} /> 
            </Routes>

        </div>
        </Router>
    )
};
export default AppRender;
