// import { BrowserRouter, Route, Routes } from "react-router-dom";
// import Dashboard from "./components/Dashboard";
import { useState } from "react";
import Axios from "axios";
// import LoginForm from "./components/LoginForm";
// import Navbar from "./comxponents/Navbar";
// import Register from "./components/Register";

//inline styling instead of css classes css attributes must be in camelCase 
/** JUST FOR FUTURE REFERENCE
 * 4 ways to insert css into js files 1) seperate css file eg) app.css 2) inline styling in js file 3) css modules eg) app.module.css 4) css in js libraries
 */
const backgroundImage = {
  backgroundImage: "url(https://www.google.com/search?q=space+images+royalty+free&rlz=1C5CHFA_enCA958CA958&sxsrf=APq-WBuIRMBP_1rKpBjDbEHIVkAF4hHUXg:1650004781801&source=lnms&tbm=isch&sa=X&ved=2ahUKEwjYkZbnupX3AhXBN30KHaksDYYQ_AUoAXoECAEQAw&biw=1792&bih=931&dpr=2#imgrc=pqGPUDigunaqJM)"
}
const heading = {
  color: 'navy',
  textAlign: 'center',
  fontSize: '60px',
  fontFamily: 'Courier New '
}

const userPassText = {
  color: 'navy',
  textAlign: 'left',
  fontSize: '20px',
  fontFamily: 'Courier New',
  marginLeft: '100px'
}


const inputBarsRegistration = {
  width: '60%',
  padding: '12px 15px',
  margin: '8px 0',
  boxSizing: 'border-box',
  float: 'initial',
  marginTop: '50px',
  marginLeft: '115px'
}

const inputBarsLogin = {
  width: '60%',
  padding: '12px 15px',
  margin: '8px 0',
  boxSizing: 'border-box',
  float: 'initial',
  marginTop: '50px',
  marginLeft: '320px'
}

const regButton = {
  backgroundColor: 'white',
  color: 'black',
  border: '2px solid #555555',
  transitionDuration:' 0.4s'
}

function App() {
    /**
     * 2 states: Registration and Login
     * Grab info from whatever we put into inputs and then validate it 
     */

    const [usernameReg, setUsernameReg] = useState('')
    const [passwordReg, setPasswordReg] = useState('')

    const[ usernameLogin, setUsernameLogin] = useState('')
    const[ passwordLogin, setPasswordLogin] = useState('')

    // const[loginStatus, setLoginStatus ] = useState('') 


    // function when we click on the register button sends all our info to backend so it inserts to our db
    const register = () => {
      console.log("making the call client");
      Axios.post("http://localhost:3001/register", {
        username: usernameReg, 
        password: passwordReg
      }).then((response) => {
        console.log(response);
      });
    };

       // function when we click on the login button sends all our info to backend so we can compare if password username combo matches
      const login = () => {
       console.log("making the call client");
        Axios.post("http://localhost:3001/login", {
          username: usernameLogin, 
          password: passwordLogin
        }).then((response) => {
          // if(response.data.errormessage){//if an error exists ie) errmsg is sent then display it on webpage
          //   setLoginStatus(response.data.message)
          // }   
          // else{//if no error r
          //   setLoginStatus(response.data[0].username)
          // }
          console.log(response.data);
        });
      };

    // const register = () => {
    //   Axios.get("http://localhost:3001/blah").then((response) => {
    //     console.log(response);
    //   });
    // };

  return (

        // <LoginForm/>
        <div style = {backgroundImage} className = "LoginForm">

          <div className = "Registration">
            <h1 style = {heading}>Registration</h1>
            <label style = {userPassText}>Username</label>
            <input 
                  style = {inputBarsRegistration} 
                  type = "text" 
                  onChange = {(e)=> {setUsernameReg(e.target.value);}}
            />
            <br/>
            <label style = {userPassText}>Password</label>
            <input
                  style = {inputBarsRegistration} 
                  type = "password"
                  onChange = {(e) => {setPasswordReg(e.target.value);}}
            />
            <br/>
            <button 
                  style = {regButton}
                  onClick = {register}>Register
            </button>
          </div>

          <div className = "Login">
              <h1 style = {heading}>Login</h1>
              <label style = {heading}>       </label>
              <input 
                    style = {inputBarsLogin}
                    type = "text" 
                    placeholder = "Username..."
                    onChange = {(e)=> {setUsernameLogin(e.target.value);}}
              />
              <br/>
              <input 
                    style = {inputBarsLogin}
                    type = "password" 
                    placeholder = "Password..."
                    onChange = {(e)=> {setPasswordLogin(e.target.value);}}
              />
              <br/>
              <button onClick = {login}>Login</button>
          </div>
          {/* <h1>{loginStatus}</h1> */}

        </div>
  );
}

export default App;
