    render() {
      return (
        <div>
          <form onSubmit={this.handleSubmit}>
            <label>
              Email or username
              <input
                name="emailOrUsername"
                type="text"
                value={this.state.emailOrUsername}
                onChange={this.handleInputChange}
              />
            </label>
            <label>
              Password
              <input
                name="password"
                type="password"
                value={this.state.password}
                onChange={this.handleInputChange}
              />
            </label>
            <button type="submit">Log in</button>
          </form>
        </div>
      );
    }
} export default LoginForm;
