import React, {useState} from 'react';


const ToDoForm =({addTask})=>{

    const [ userInput, setUserInput ] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        addTask(userInput);
        setUserInput("");
    }

    return(
        <div>
            <form onSubmit={ e.preventDefault(),addTask(userInput),setUserInput("")}>
                <input 
                    value={userInput} 
                    type="text" 
                    onChange={setUserInput(e.currentTarget.value)} 
                    placeholder="Enter task..."
                />
                <button>Submit</button>
            </form>
        </div>
    )

}
export default ToDoForm;
