import {BrowserRouter as Router,Switch,Route,NavLink} from "react-router-dom";
import { useState } from "react";
import Axios from "axios";

//styles
const heading = {
  color: 'navy',
  textAlign: 'center',
  fontSize: '40px',
  fontFamily: 'Courier New '
}

const userPassText = {
  color: 'navy',
  textAlign: 'center',
  fontSize: '18px',
  fontFamily: 'Courier New',
  marginLeft: '100px'
}

const inputBarsRegistration = {
  width: '40%',
  padding: '12px 15px',
  margin: '8px 0',
  boxSizing: 'border-box',
  float: 'initial',
  marginTop: '50px',
  marginLeft: '315px'
}

const inputBarsLogin = {
  width: '40%',
  padding: '12px 15px',
  margin: '8px 0',
  boxSizing: 'border-box',
  float: 'initial',
  marginTop: '50px',
  marginLeft: '510px'
}

const regLogButton = {
  transitionDuration:' 0.4s',
  transform: 'translateY(4px)',
  fontFamily: 'Courier New',
  marginLeft: '795px',
  width: '10%',
  padding: '8px 18px',
  fontSize: '16px'

}

//hooks
const [usernameReg, setUsernameReg] = useState('')
const [passwordReg, setPasswordReg] = useState('')
const[ usernameLogin, setUsernameLogin] = useState('')
const[ passwordLogin, setPasswordLogin] = useState('')
const[loginStatus, setLoginStatus ] = useState('') 
const[regStatus, setRegStatus] = useState('')

// function when we click on the register button sends all our info to backend so it inserts to our db
const register = () => {
    console.log("making the call client");
    Axios.post("http://localhost:3001/register", {
      username: usernameReg, 
      password: passwordReg
    }).then((response) => {
    if(response.data.message){
        console.log("username taken")
        setRegStatus(response.data.message)
    }
    else{
        console.log("succesfully created account")
        setRegStatus("Account Created")
    }
        console.log(response);
    });
};

// function when we click on the login button sends all our info to backend so we can compare if password username combo matches
const login = () => {
    console.log("making the call client");
     Axios.post("http://localhost:3001/login", {
       username: usernameLogin, 
       password: passwordLogin
     }).then((response) => {
       if(response.data.errormessage){ //if an error exists ie) errmsg is sent then display it on webpage
         console.log('wrong user/pass')
         setLoginStatus(response.data.errormessage)
       }
       else{//if no error
         // console.log("Printing response==> "+response.data[0].username);
         console.log('sucessful login')
         setLoginStatus(response.data[0].username)
       }
       // console.log(response.data);
     });
};



const loginPage = () => (
    <div>
      <h2>loginPage</h2>
    </div>
  );

  const registrationPage = () => (
    <div>
      <h2>registrationPage</h2>
    </div>
  );

const AppRender = () => (
    <Router>
    <div>
        <ul>
          <li>
            <Link to="/loginPage">Login</Link>
          </li>
          <li>
            <Link to="/registrationPage">Registration</Link>
          </li>
        </ul>

        <hr />

        <Route exact path="/loginPage" component={loginPage} />
        <Route path="/registrationPage" component={registrationPage} />
    </div>
    </Router>
  );
  export default AppRender;
